import React, { useState } from 'react'
import api from '../../api'

export default function Signup(props) {
  const [state, setState] = useState({
    country: '',
    countryOfWork: '',
    firstName: '',
    lastName: '',
    holidayAllowance: ' ',
  })

  function handleInputChange(event) {
    console.log('entrooooo')
    setState({
      ...state,
      [event.target.name]: event.target.value,
    })
  }

  function handleClick(e) {
    e.preventDefault()
    let data = {
      username: state.username,
      name: state.name,
      password: state.password,
      accountNumber: "Must Submit when ehtereum wallet intergration is on",
      profilePictureUrl: "www.fcebook.cmo",
      email: state.email,
      pictureHashes: "29343903",
      bio: state.bio,
      dob: state.dob
    }
    api
      .signup(data)
      .then(result => {
        console.log('SUCCESS!')
        props.history.push('/') // Redirect to the home page
      })
      .catch(err => setState({ message: err.toString() }))
  }


  if (state.country == 'Spain') {
    return (
      <div className="Signup">
        <h2>Spain</h2>
        <form>
          Username:{' '}
          <input
            type="text"
            value={state.username}
            name="username"
            onChange={handleInputChange}
          />{' '}
          <br />
        Name:{' '}
          <input
            type="text"
            value={state.name}
            name="name"
            onChange={handleInputChange}
          />{' '}
          <br />
        Password:{' '}
          <input
            type="password"
            value={state.password}
            name="password"
            onChange={handleInputChange}
          />{' '}
          <br />
          <button onClick={e => handleClick(e)}>Signup</button>
        </form>
        {state.message && <div className="info info-danger">{state.message}</div>}
      </div>
    )
  }

  if (state.country == "Ghana") {
    return (
      <div className="Signup">
        <h2>Ghana</h2>
        <form>
          Username:{' '}
          <input
            type="text"
            value={state.username}
            name="username"
            onChange={handleInputChange}
          />{' '}
          <br />
    Name:{' '}
          <input
            type="text"
            value={state.name}
            name="name"
            onChange={handleInputChange}
          />{' '}
          <br />
    Password:{' '}
          <input
            type="password"
            value={state.password}
            name="password"
            onChange={handleInputChange}
          />{' '}
          <br />
          <button onClick={e => handleClick(e)}>Signup</button>
        </form>
        {state.message && <div className="info info-danger">{state.message}</div>}
      </div>
    )
  }

  if (state.country == "Brazil") {
    return (
      <div className="Brazil">
        <h2>Brazil</h2>
        <form>
          Username:{' '}
          <input
            type="text"
            value={state.username}
            name="username"
            onChange={handleInputChange}
          />{' '}
          <br />
    Name:{' '}
          <input
            type="text"
            value={state.name}
            name="name"
            onChange={handleInputChange}
          />{' '}
          <br />
    Password:{' '}
          <input
            type="password"
            value={state.password}
            name="password"
            onChange={handleInputChange}
          />{' '}
          <br />
          <button onClick={e => handleClick(e)}>Signup</button>
        </form>
        {state.message && <div className="info info-danger">{state.message}</div>}
      </div>
    )
  }
  return (
    <div className="Signup">
      <h2>Signup</h2>
      <form>


        <select class="form-select" aria-label="Default select example" onChange={handleInputChange}>

          <option value="Spain" name="Spain"  >Spain</option>
          <option value="Ghana" name="Ghana"  >Ghana</option>
          <option value="Brazil" name="Brazil" >Brazil</option>

        </select>
        Username:{' '}
        <input
          type="text"
          value={state.username}
          name="username"
          onChange={handleInputChange}
        />{' '}
        <br />
        Name:{' '}
        <input
          type="text"
          value={state.name}
          name="name"
          onChange={handleInputChange}
        />{' '}
        <br />
        Password:{' '}
        <input
          type="password"
          value={state.password}
          name="password"
          onChange={handleInputChange}
        />{' '}
        <br />
        <button onClick={e => handleClick(e)}>Signup</button>
      </form>
      {state.message && <div className="info info-danger">{state.message}</div>}
    </div>
  )
}
